+            SnykClient client = clientFactory.get(query.getIntegrationKey());
+            SnykApiListOrgsResponse orgsResponse = client.getOrgs();
+            for(SnykApiOrg currentOrg : orgsResponse.getOrgs()){
+                SnykApiListProjectsResponse projectsResponse = client.getProjects(currentOrg.getId());
+                for(SnykApiProject currentProject : projectsResponse.getProjects()){
+                    SnykApiListIssuesResponse issuesResponse = client.getIssues(currentOrg.getId(), currentProject.getId());
+                    snykIssues.add(map(currentOrg.getId(), currentProject.getId(), issuesResponse));
+                }
+            }
+        } catch (SnykClientException e) {
+            throw new FetchException("Could not fetch Synk Issues", e);
+        }
+
+        return snykIssues.stream()
+                .map(BasicData.mapper(SnykIssues.class));
+    }
+
+    public static SnykIssues map(final String orgId, final String projectId, final SnykApiListIssuesResponse apiIssues){
+        SnykIssues.SnykIssuesBuilder bldr =  SnykIssues.builder();
+        bldr.orgId(orgId);
+        bldr.projectId(projectId);
+        bldr.dependencyCount(apiIssues.getDependencyCount());
+        bldr.packageManager(apiIssues.getPackageManager());
+        bldr.issues(mapIssues(orgId, projectId, apiIssues.getIssues()));
+        return bldr.build();
+    }
+
+    public static SnykIssues.Issues mapIssues(final String orgId, final String projectId, final SnykApiListIssuesResponse.Issues apiIssues){
+        SnykIssues.Issues.IssuesBuilder bldr = SnykIssues.Issues.builder();
+        bldr.vulnerabilities(mapVulnarabilities(orgId, projectId, apiIssues.getVulnerabilities()));
+        bldr.licenses(mapLicenses(orgId, projectId, apiIssues.getLicenses()));
+        SnykIssues.Issues issues = bldr.build();
+        return issues;
+    }
+
+    public static List<SnykVulnerability> mapVulnarabilities(final String orgId, final String projectId, final List<SnykApiVulnerability> apiVulnerabilities){
+        if(apiVulnerabilities == null){
+            return null;
+        }
+        List<SnykVulnerability> vulnerabilities = new ArrayList<>();
+        for (SnykApiVulnerability apiVulnerability : apiVulnerabilities){
+            SnykVulnerability.SnykVulnerabilityBuilder bldr = SnykVulnerability.builder();
+            bldr.orgId(orgId);
+            bldr.projectId(projectId);
+
+            bldr.id(apiVulnerability.getId());
+            bldr.url(apiVulnerability.getUrl());
+            bldr.title(apiVulnerability.getTitle());
+            bldr.type(apiVulnerability.getType());
+            bldr.description(apiVulnerability.getDescription());
+            bldr.from(copyListOfString(apiVulnerability.getFrom()));
+            bldr.packageName(apiVulnerability.getPackageName());
+            bldr.version(apiVulnerability.getVersion());
+            bldr.severity(apiVulnerability.getSeverity());
+            bldr.exploitMaturity(apiVulnerability.getExploitMaturity());
+            bldr.language(apiVulnerability.getLanguage());
+            bldr.packageManager(apiVulnerability.getPackageManager());
+
+            SnykVulnerability.Semver semver = SnykVulnerability.Semver.builder()
+            .unaffected(apiVulnerability.getSemver().getUnaffected())
+            .vulnerable(apiVulnerability.getSemver().getVulnerable())
+            .build();
+            bldr.semver(semver);
+
+            bldr.publicationTime(apiVulnerability.getPublicationTime());
+            bldr.disclosureTime(apiVulnerability.getDisclosureTime());
+            bldr.upgradable(apiVulnerability.getUpgradable());
+            bldr.pinnable(apiVulnerability.getPinnable());
+            bldr.patchable(apiVulnerability.getPatchable());
+
+            SnykVulnerability.Identifiers identifiers = SnykVulnerability.Identifiers.builder()
+                    .cve(copyListOfString(apiVulnerability.getIdentifiers().getCve()))
+                    .cwe(copyListOfString(apiVulnerability.getIdentifiers().getCwe()))
+                    .alternative(copyListOfString(apiVulnerability.getIdentifiers().getAlternative()))
+                    .build();
+            bldr.identifiers(identifiers);
+
+            bldr.credit(copyListOfString(apiVulnerability.getCredit()));
+            bldr.cvssv3(apiVulnerability.getCvssv3());
+            bldr.cvssScore(apiVulnerability.getCvssScore());
+
+            bldr.patches(copyPatches(apiVulnerability.getPatches()));
+
+            bldr.ignored(apiVulnerability.getIgnored());
+            bldr.patched(apiVulnerability.getPatched());
+            bldr.upgradePath(copyListOfString(apiVulnerability.getUpgradePath()));
+
+            SnykVulnerability vulnerability = bldr.build();
+            vulnerabilities.add(vulnerability);
+        }
+        return vulnerabilities;
+    }
+
+    public static List<SnykVulnerability.Patch> copyPatches(final List<SnykApiVulnerability.Patch> apiPatches){
+        if(apiPatches == null){
+            return null;
+        }
+        List<SnykVulnerability.Patch> patches = new ArrayList<>();
+        for(SnykApiVulnerability.Patch currentPatch : apiPatches){
+            SnykVulnerability.Patch.PatchBuilder bldr = SnykVulnerability.Patch.builder();
+            bldr.id(currentPatch.getId());
+            bldr.urls(copyListOfString(currentPatch.getUrls()));
+            bldr.version(currentPatch.getVersion());
+            bldr.comments(copyListOfString(currentPatch.getComments()));
+            bldr.modificationTime(currentPatch.getModificationTime());
+            patches.add(bldr.build());
+        }
+        return patches;
+    }
+
+    public static List<String> copyListOfString (final List<String> ori){
+        if(ori == null){
+            return null;
+        }
+        List<String> res = new ArrayList<>();
+        for(String current : ori){
+            res.add(current);
+        }
+        return res;
+    }
+
+    public static List<SnykLicenseIssue> mapLicenses(final String orgId, final String projectId, final List<SnykApiLicenseIssue> apiLicenseIssues){
+        if(apiLicenseIssues == null){
+            return null;
+        }
+        List<SnykLicenseIssue> licenseIssues = new ArrayList<>();
+        for (SnykApiLicenseIssue apiLicenseIssue : apiLicenseIssues){
+            SnykLicenseIssue.SnykLicenseIssueBuilder bldr = SnykLicenseIssue.builder();
+            bldr.orgId(orgId);
+            bldr.projectId(projectId);
+
+            SnykLicenseIssue licenseIssue = bldr.build();
+            licenseIssues.add(licenseIssue);
+        }
+        return licenseIssues;
+    }
+}

