+package io.levelops.ingestion.agent.ingestion;
+
+import java.util.Date;
+
+import com.fasterxml.jackson.databind.ObjectMapper;
+
+import org.springframework.beans.factory.annotation.Autowired;
+
+import io.levelops.ingestion.controllers.ControllerIngestionResult;
+import io.levelops.ingestion.controllers.DataController;
+import io.levelops.ingestion.controllers.generic.ControllerIngestionResultList;
+import io.levelops.ingestion.exceptions.IngestException;
+import io.levelops.ingestion.integrations.pagerduty.models.PagerDutyIterativeScanQuery;
+import io.levelops.ingestion.strategies.pagination.NumberedPaginationStrategy;
+import io.levelops.ingestion.strategies.pagination.PaginationStrategy;
+import io.levelops.integrations.pagerduty.client.PagerDutyClientFactory;
+import io.levelops.integrations.pagerduty.models.PagerDutyIncident;
+import io.levelops.integrations.pagerduty.models.PagerDutyLogEntry;
+import io.levelops.integrations.pagerduty.sources.PagerDutyApiDataSource;
+import io.levelops.integrations.pagerduty.utils.PagerDutyUtils;
+import io.levelops.integrations.storage.models.StorageResult;
+import io.levelops.sinks.StorageDataSink;
+import lombok.Builder;
+import lombok.extern.log4j.Log4j2;
+
+@Log4j2
+public class PagerDutyIterativeScanController implements DataController<PagerDutyIterativeScanQuery> {
+
+    private final static int PAGE_SIZE = 50;
+
+    private final ObjectMapper objectMapper;
+    private final PaginationStrategy<PagerDutyIncident, PagerDutyIncident.Query> incidentsIngestor;
+    private final PaginationStrategy<PagerDutyLogEntry, PagerDutyLogEntry.Query> logEntriesIngestor;
+
+    @Builder
+    @Autowired
+    public PagerDutyIterativeScanController(   
+            final ObjectMapper objectMapper,
+            final PagerDutyClientFactory clientFactory, 
+            final StorageDataSink storageDataSink){
+        this.objectMapper = objectMapper;
+
+        this.incidentsIngestor = NumberedPaginationStrategy.<PagerDutyIncident, PagerDutyIncident.Query>builder()
+                .integrationType("pagerduty")
+                .dataType("incident")
+                .objectMapper(objectMapper)
+                .storageDataSink(storageDataSink)
+                .pageDataSupplier( (query, page) -> 
+                        new PagerDutyApiDataSource<PagerDutyIncident, PagerDutyIncident.Query>(clientFactory, PagerDutyIncident.class)
+                        .fetchMany(PagerDutyIncident.Query.builder()
+                                .limit(PAGE_SIZE)
+                                .since(query.getSince())
+                                .until(query.getUntil())
+                                .offset(page.getPageNumber() * PAGE_SIZE)
+                                .build()) )
+                .skipEmptyResults(true)
+                .build();
+
+        this.logEntriesIngestor = NumberedPaginationStrategy.<PagerDutyLogEntry, PagerDutyLogEntry.Query>builder()
+                .integrationType("pagerduty")
+                .dataType("log_entry")
+                .objectMapper(objectMapper)
+                .storageDataSink(storageDataSink)
+                .pageDataSupplier( (q, page) -> 
+                        new PagerDutyApiDataSource<PagerDutyLogEntry, PagerDutyLogEntry.Query>(clientFactory, PagerDutyLogEntry.class)
+                        .fetchMany(PagerDutyLogEntry.Query.builder()
+                                .limit(PAGE_SIZE)
+                                .since(q.getSince())
+                                .until(q.getUntil())
+                                .offset(page.getPageNumber() * PAGE_SIZE)
+                                .build()) )
+                .skipEmptyResults(true)
+                .build();
+    }
+
+    @Override
+    public ControllerIngestionResult ingest(String jobId, PagerDutyIterativeScanQuery query) throws IngestException {
+        String to = PagerDutyUtils.formatDate( (query.getTo() != null) ? query.getTo() : new Date());
+        // onboarding
+        if (query.getFrom() == null){
+            // get all existing incidents in any state
+            StorageResult results = incidentsIngestor.ingestAllPages(jobId, query.getIntegrationKey(), PagerDutyIncident.Query.builder()
+                                    .integrationKey(query.getIntegrationKey())
+                                    .until(to)
+                                    .limit(PAGE_SIZE)
+                                    .offset(0)
+                                    .build());
+
+            return new ControllerIngestionResultList(results);
+        }
+
+        // Iterative
+        String from = PagerDutyUtils.formatDate(query.getFrom());
+        // get new incidents
+        StorageResult incidentsResults = incidentsIngestor.ingestAllPages(jobId, query.getIntegrationKey(), PagerDutyIncident.Query.builder()
+                                .integrationKey(query.getIntegrationKey())
+                                .limit(PAGE_SIZE)
+                                .offset(0)
+                                .since(from)
+                                .until(to)
+                                .build());
+        // get logs with incidents
+        StorageResult logResults = logEntriesIngestor.ingestAllPages(jobId, query.getIntegrationKey(), PagerDutyLogEntry.Query.builder()
+                                    .limit(PAGE_SIZE)
+                                    .offset(0)
+                                    .since(from)
+                                    .until(to)
+                                    .build());
+
+        return new ControllerIngestionResultList(incidentsResults, logResults);
+    }
+
+    @Override
+    public PagerDutyIterativeScanQuery parseQuery(Object o) {
+        return objectMapper.convertValue(o, PagerDutyIterativeScanQuery.class);
+    }
+
+}
