+import io.levelops.ingestion.controllers.generic.BaseIntegrationQuery;
+import io.levelops.ingestion.controllers.generic.ControllerIngestionResultList;
+import io.levelops.ingestion.controllers.generic.IntegrationController;
+import io.levelops.ingestion.data.Data;
+import io.levelops.ingestion.exceptions.FetchException;
+import io.levelops.ingestion.exceptions.IngestException;
+import io.levelops.ingestion.sources.DataSource;
+import io.levelops.ingestion.strategies.StorageStrategy;
+import io.levelops.ingestion.strategies.pagination.StreamedPaginationStrategy;
+import io.levelops.integrations.snyk.models.SnykOrg;
+import io.levelops.integrations.snyk.models.SnykProject;
+import io.levelops.integrations.snyk.sources.SnykOrgDataSource;
+import io.levelops.integrations.snyk.sources.SnykProjectDataSource;
+import io.levelops.integrations.storage.models.StorageResult;
+import io.levelops.sinks.StorageDataSink;
+import lombok.Builder;
+import lombok.extern.log4j.Log4j2;
+import org.apache.commons.lang3.Validate;
+import org.apache.logging.log4j.util.Strings;
+
+import java.util.Collections;
+import java.util.List;
+import java.util.Objects;
+import java.util.stream.Stream;
+
+@Log4j2
+public class SnykIterativeScanController implements DataController<BaseIntegrationQuery> {
+    private final ObjectMapper objectMapper;
+    private final IntegrationController<BaseIntegrationQuery> issuesController;
+    private final SnykOrgDataSource orgDataSource;
+    private SnykProjectDataSource snykProjectDataSource;
+    private final StorageStrategy storageStrategy;
+
+    @Builder
+    public SnykIterativeScanController(ObjectMapper objectMapper,
+                                       IntegrationController<BaseIntegrationQuery> issuesController,
+                                       SnykOrgDataSource orgDataSource,
+                                       StorageDataSink storageDataSink,
+                                       SnykProjectDataSource snykProjectDataSource) {
+
+        this.objectMapper = objectMapper;
+        this.issuesController = issuesController;
+        this.orgDataSource = orgDataSource;
+        this.snykProjectDataSource = snykProjectDataSource;
+        storageStrategy = new StorageStrategy(objectMapper, storageDataSink);
+
+    }
+
+
+    @Override
+    public ControllerIngestionResult ingest(String jobId, BaseIntegrationQuery query) throws IngestException {
+
+        // ORGS
+        Stream<Data<SnykOrg>> orgsStream = orgDataSource.fetchMany(query);
+
+        List<SnykOrg> data = Data.collect(orgsStream);
+        if (data.isEmpty()) {
+            return new EmptyIngestionResult();
+        }
+
+        StorageResult orgsStorageResult = storageStrategy.storeOnePage(
+                query.getIntegrationKey(),
+                "snyk",
+                "orgs",
+                jobId,
+                ListResponse.of(data),
+                null);// not paginated
+
+        // PROJECTS
+
+        Stream<Data<SnykProject>> projectStream = data.stream()
+                .map(SnykOrg::getId)
+                .filter(Strings::isNotEmpty)
+                .flatMap(orgId -> {
+                    try {
+                        return snykProjectDataSource.fetchMany(SnykProjectDataSource.SnykProjectQuery.builder()
+                                .orgId(orgId)
+                                .integrationKey(query.getIntegrationKey())
+                                .build());
+                    } catch (FetchException e) {
+                        log.warn("Failed to fetch projects for org={}", orgId, e);
+                        return Stream.empty();
+                    }
+                }).filter(Objects::nonNull);
+
+        List<SnykProject> projectsData = Data.collect(projectStream);
+
+        StorageResult projectsStorageResult = storageStrategy.storeOnePage(
+                query.getIntegrationKey(),
+                "snyk",
+                "projects",
+                jobId,
+                ListResponse.of(projectsData),
+                null);// not paginated
+
+
+        // ISSUES
+
+
+        ControllerIngestionResult issuesResult = issuesController.ingest(jobId, query);
+
+
+        return new ControllerIngestionResultList(issuesResult, orgsStorageResult, projectsStorageResult);
+    }
+
+    @Override
+    public BaseIntegrationQuery parseQuery(Object o) {
+        return objectMapper.convertValue(o, BaseIntegrationQuery.class);
+    }
+}

