+        bldr.name(apiProject.getName());
+        bldr.id(apiProject.getId());
+        bldr.created(apiProject.getCreated());
+        bldr.origin(apiProject.getOrigin());
+        bldr.type(apiProject.getType());
+        bldr.readOnly(apiProject.getReadOnly());
+        bldr.testFrequency(apiProject.getTestFrequency());
+        bldr.totalDependencies(apiProject.getTotalDependencies());
+
+        SnykProject.IssueCountsBySeverity issueCountsBySeverity = SnykProject.IssueCountsBySeverity.builder()
+        .high(apiProject.getIssueCountsBySeverity().getHigh())
+        .medium(apiProject.getIssueCountsBySeverity().getMedium())
+        .low(apiProject.getIssueCountsBySeverity().getLow()).build();
+
+        bldr.issueCountsBySeverity(issueCountsBySeverity);
+        bldr.imageId(apiProject.getImageId());
+        bldr.imageTag(apiProject.getImageTag());
+        bldr.lastTestedDate(apiProject.getLastTestedDate());
+
+        SnykProject.User importingUser = SnykProject.User.builder()
+        .id(apiProject.getImportingUser().getId())
+        .name(apiProject.getImportingUser().getName())
+        .email(apiProject.getImportingUser().getEmail()).build();
+
+        bldr.importingUser(importingUser);
+
+        SnykProject.User owner = SnykProject.User.builder()
+                .id(apiProject.getOwner().getId())
+                .name(apiProject.getOwner().getName())
+                .email(apiProject.getOwner().getEmail()).build();
+
+        bldr.owner(owner);
+        return bldr.build();
+    }
+
+    @Value
+    @Builder
+    @JsonDeserialize(builder = SnykProjectQuery.SnykProjectQueryBuilder.class)
+    public static class SnykProjectQuery implements IntegrationQuery {
+
+        @JsonProperty("integration_key")
+        IntegrationKey integrationKey;
+
+        /**
+         * Needed to fetch one specific project. Both Key and Id work.
+         */
+        @JsonProperty("org_id")
+        String orgId;
+    }
+}

