+import React from 'react';
+import { withRouter } from 'react-router-dom';
+import * as PropTypes from 'prop-types';
+import { Card, Input, Label, IconButton, Modal } from 'shared-resources/components';
+import './product-stage.style.scss';
+import {SvgIcon} from "../../../shared-resources/components";
+
+export class ProductStageComponent extends React.PureComponent {
+
+    constructor(props) {
+        super(props);
+        this.state={
+            collapsed: true,
+            show_integration_modal: false
+        };
+        this.handleIntegrationFlow = this.handleIntegrationFlow.bind(this);
+        this.buildIntegrationModal = this.buildIntegrationModal.bind(this);
+    }
+
+
+    handleIntegrationFlow(e) {
+        e.preventDefault();
+        this.props.onMapIntegration(this.props.stage);
+    }
+
+    buildIntegrationModal() {
+        return(
+            <Modal
+                title={<Label type={"link"} text={`${this.props.stage} Integration Mappings`}/>}
+                onCloseEvent={()=>{this.setState({show_integration_modal: false})}}
+            >
+                This is where I will be adding my integrations
+            </Modal>
+        );
+    }
+
+    render() {
+        const {className, stage, collapsed, expandedComponent} = this.props;
+        let icon = collapsed?"plus":"minus";
