+import React from 'react';
+import { withRouter } from 'react-router-dom';
+import * as PropTypes from 'prop-types';
+import { Card, Input, Label, IconButton, Modal } from 'shared-resources/components';
+import './product-stage.style.scss';
+
+export class ProductStageComponent extends React.PureComponent {
+
+    constructor(props) {
+        super(props);
+        this.state={
+            collapsed: true,
+            show_integration_modal: false
+        };
+        this.handleIntegrationFlow = this.handleIntegrationFlow.bind(this);
+        this.buildIntegrationModal = this.buildIntegrationModal.bind(this);
+        this.handleAddNewIntegration = this.handleAddNewIntegration.bind(this);
+    }
+
+
+    handleIntegrationFlow(e) {
+        e.preventDefault();
+        this.props.onMapIntegration(this.props.stage);
+    }
+
+    buildIntegrationModal() {
+        return(
+            <Modal
+                title={<Label type={"link"} text={`${this.props.stage} Integration Mappings`}/>}
+                onCloseEvent={()=>{this.setState({show_integration_modal: false})}}
+            >
+                This is where I will be adding my integrations
+            </Modal>
+        );
+    }
+
+    handleAddNewIntegration(e) {
+        e.preventDefault();
+        this.props.history.push(
+            `/admin/configuration/integrations?return=/admin/products/edit-product-page?step=1&product=${this.props.stage.product_id}`
+        );
+    }
+
+    render() {
+        const {className, stage, collapsed, expandedComponent, integrations} = this.props;
+        let icon = collapsed?"plus":"minus";
+        let integrationsAvailable = integrations.filter(integ => stage.integration_types.includes(integ.application));
+        return(
+            <div className={`${className} ${className}__container flex direction-column justify-start`}>
+                <div className={`flex direction-row justify-space-between`}>
+                    <div>
+                        <IconButton icon={icon}
+                                    style={{ width: '1.2rem', height: '1.2rem' }}
+                                    onClickEvent={this.props.onExpand}
+                        />
+                       <Label type={"title"} text={stage.name.replace("_"," ").toUpperCase()}/>
+                    </div>
+                    <div>
+                        <a href={"#"} onClick={this.handleAddNewIntegration} style={{margin: "10px"}}>
+                            <Label type={"link"} text={"add integrations"}/>
+                        </a>
+                        <Label type={"badge"} text={this.props.mappings.length} style={{margin: "10px"}}/>
+                        {
+                            integrationsAvailable.length > 0 &&
+                            <a href={"#"} onClick={this.handleIntegrationFlow} style={{margin: "10px"}}>
+                                <Label type={"link"} text={"map integrations"}/>
+                            </a>
+                        }
+                    </div>
+                </div>
+                <div>
+                    {!collapsed && expandedComponent}
+                </div>
+            </div>
+        );
+    }
+}
+
+
+ProductStageComponent.propTypes = {
+    className: PropTypes.string,
+    stage: PropTypes.string.isRequired,
+    mappings: PropTypes.array.isRequired,
+    collapsed: PropTypes.bool.isRequired,
+    expandedComponent: PropTypes.element.isRequired,
+    onExpand: PropTypes.func.isRequired,
+    onMapIntegration: PropTypes.func.isRequired
+};
+
+ProductStageComponent.defaultProps = {
+    className: 'product-stage',
+    collapsed: true,
+    mappings: [],
+    expandedComponent: <div>Expanded</div>
+};
+
+export default withRouter(ProductStageComponent);

