+                !getError(props.rest_api, "mappings", method,id)) {
+                props.formClear("github_mapping_form");
+                props.onDone();
+                return({
+                    ...state,
+                    create_loading: false
+                })
+            }
+        }
+    }
+
+    handleCancel(e) {
+        this.props.restapiClear("mappings");
+        this.props.onDone();
+    }
+
+    handleAdd(e) {
