+                        <Nav bsStyle="tabs" >
+                            {
+                                this.props.stage.integration_types.map(
+                                    integration_type => (
+                                        <NavItem eventKey={integration_type} onClick={
+                                            e => {this.setState({
+                                                integration_type: integration_type,
+                                                loading: true
+                                            }, () => this.props.mappingsList(
+                                                {
+                                                    filter: {
+                                                        stage_ids: [this.props.stage.id],
+                                                        integration_type: integration_type
+                                                    }
+                                                }
+                                            ));
+                                            }}>
+                                            <div className={`flex direction-row justify-start`}>
+                                                <SvgIcon
+                                                    icon={integration_type}
+                                                    style={{ width: '2.4rem', height: '2.4rem', paddingRight: "2px" }}/>
+                                                <Label text={integration_type.toUpperCase()} type={"description"}/>
+                                            </div>
+                                        </NavItem>
+                                    )
+                                )
+                            }
+                        </Nav>
+                    </Tab.Container>
+                </div>
+                <div style={{width: "40%", marginTop: "10px"}}>
+                    <SelectWrapper label={"Integration"}>
+                        <SearchableSelect
+                            method={`list`}
+                            uri={`integrations`}
+                            searchField="name"
+                            rest_api={this.props.rest_api}
+                            fetchData={this.props.integrationsList}
+                            moreFilters={{applications:[this.state.integration_type]}}
+                            additionalField={"application"}
+                            id={'select-type'}
+                            value={this.state.integration_select}
