+                let previewItems = state.preview_items.filter(
+                    item => item.label !== "integrations"
+                );
+                previewItems.push({
+                    label: "integrations",
+                    value: <div className={`flex direction-row justify-start`}>
+                        {   props.rest_api.mappings.list["0"].data.records.map(
