+import React from 'react';
+import * as PropTypes from 'prop-types';
+import { connect } from 'react-redux';
+import { debounce } from 'lodash';
+
+import ServerPaginatedTable from 'components/Table/ServerPaginatedTable';
+import { mapRestapiDispatchtoProps, mapRestapiStatetoProps } from 'redux/maps/restapiMap';
+import { Card, Input } from 'shared-resources/components';
+import { Title } from 'shared-resources/helpers';
+import { tableCell } from 'utils/tableUtils';
+import { tableColumns } from './table-config';
+
+import './signature_list.style.scss';
+
+export class SignaturesListPage extends React.PureComponent {
+  constructor(props) {
+    super(props);
+
+    this.state = {
+      highlighted_row: null,
+      partial_filters: {},
+    };
+
+    this.onRowClickHandler = this.onRowClickHandler.bind(this);
+    this.onSearchBySignatureHandler = this.onSearchBySignatureHandler.bind(this);
+    this.debounceSearchHandler = debounce(this.onFetchFilteredData, 300);
+    this.onFetchFilteredData = this.onFetchFilteredData.bind(this);
+
+  }
+
+  componentWillUnmount() {
+    this.props.restapiClear('signatures', 'list', '0');
+  }
+
+  onRowClickHandler(e, t, rowInfo) {
+    this.setState({
+      highlighted_row: rowInfo.original.id,
+    })
+  }
+
+  onSearchBySignatureHandler(value) {
+    this.setState({ search_term: value });
+    this.debounceSearchHandler(value);
+  }
+
+  onFetchFilteredData(value) {
+    this.setState({ partial_filters: { policy: value } })
+  }
+
+  render() {
+    const { className } = this.props;
+    const PAGE_ERROR = 'Could not fetch signatures';
+
+    const mappedColumns = tableColumns.map((column) => {
+      if (['tags', 'products', 'integration_types', 'enabled'].includes(column.id)) {
+        return {
+          ...column,
+          Cell: props => tableCell(column.id, props.value)
+        }
+      }
+      return column;
+    });
+    return (
+      <div className={className}>
+        <Card>
+          <div className={`${className} flex direction-column`}>
+            <Title
+              title="Signatures"
+              button={false}
+            />
+            <div>
+              <hr/>
+            </div>
+            <div className={`${className}__search-bar`}>
+              <Input
+                onChangeEvent={this.onSearchBySignatureHandler}
+                name="search-signatures"
+                id="search-signatures"
+                value={this.state.search_term}
+                label="Search by signature"
+              />
+            </div>
+            <ServerPaginatedTable
+              restCall="getSignatures"
+              backendErrorMessage={PAGE_ERROR}
+              columns={mappedColumns}
+              getTrProps={(state, rowInfo) => {
+                return {
+                  onMouseOver: (e, t) => {
+                    this.onRowClickHandler(e, t, rowInfo);
+                  },
+                };
+              }}
+              partialFilters={this.state.partial_filters}
+            />
+          </div>
+        </Card>
+      </div>
+    );
+  }
+}
+
+SignaturesListPage.propTypes = {
+  className: PropTypes.string,
+};
+
+SignaturesListPage.defaultProps = {
+  className: 'signatures-list',
+};
+
+export default connect(mapRestapiStatetoProps, mapRestapiDispatchtoProps)(SignaturesListPage);

