+import * as React from 'react'
+import { FlowChart, IChart, IConfig, IFlowChartComponents, actions } from "@mrblenny/react-flow-chart";
+// @ts-ignore
+import { mapValues } from 'lodash';
+
+export interface IFlowChartWithStateProps {
+    initialValue: IChart
+    Components?: IFlowChartComponents
+    config?: IConfig,
+    callbacks?: any
+}
+
+/**
+ * Flow Chart With State
+ */
+export class FlowChartWithState extends React.Component<IFlowChartWithStateProps, IChart> {
+    public state: IChart;
+
+    constructor (props: IFlowChartWithStateProps) {
+        super(props);
+        this.state = props.initialValue;
+    }
+
+    public render () {
+        const { Components, config, callbacks} = this.props;
+        const stateActions = mapValues(callbacks, (func: any) =>
+            (...args: any) => this.setState(func(...args))) as typeof actions;
+        return (
+            <FlowChart
+                chart={this.state}
+                callbacks={stateActions}
+                Components={Components}
+                config={config}
+            />
+        )
+    }
+}
