+import React from 'react';
+import * as PropTypes from 'prop-types';
+import { connect } from 'react-redux';
+import {update, get, unset} from 'lodash';
+import {Row, Col, Drawer, Button, Form, Select, Input, Timeline, Icon, Typography, Tree, Tag} from 'antd';
+import "./conditions.style.scss";
+const uuidv1 = require('uuid/v1');
+
+const { Option } = Select;
+const { Text } = Typography;
+const { TreeNode } = Tree;
+
+const DEFAULT_DATA = {
+    "type": "condition",
+    "name": "or",
+    "id": "1",
+    "path": "",
+    "children": [
+        {"type": "rule", "id": "2" , path: "1"},
+        {
+            "type": "condition",
+            "name": "and",
+            id: "77",
+            "children":[
+                {"type": "rule", "id": "3", "subject":{"id":"1", "name":"Signature 1"}, "operator":"==", "predicate":"true"},
+                {"type": "rule", "id": "4"},
+                {
+                    "type":"condition",
+                    "name": "and",
+                    "id": "5",
+                    "children": [
+                        {"type": "rule", "id": "6"}
+                    ]
+                }
+            ]
+        }
+    ]
+};
+
+export class ConditionsComponent extends React.Component {
+
+    constructor(props) {
+        super(props);
+        this.state = {
+            data: DEFAULT_DATA
+        };
+
+        this.buildConditions = this.buildConditions.bind(this);
+        this.buildRule = this.buildRule.bind(this);
+        this.addRule = this.addRule.bind(this);
+        this.deleteCondition = this.deleteCondition.bind(this);
+        this.addCondition = this.addCondition.bind(this);
+    }
+
+
+    buildRule(item, path) {
+        return(
+            <TreeNode key={path.concat(`.id=${item.id}`)} isLeaf={true} selectable={false}
+                      title={
+                <Row gutter={[5,10]} type={"flex"} justify={"center"} align={"middle"}>
+                    <Col span={12}>
+                        <Select style={{width: "100%"}} placeholder={"Select Signature"}>
+                            <Option key={1}>Signature 1</Option>
+                            <Option key={2}>Signature 2</Option>
+                            <Option key={3}>Signature 3</Option>
+                            <Option key={4}>Signature 4</Option>
+                            <Option key={5}>Signature 5</Option>
+                        </Select>
+                    </Col>
+                    <Col span={6}>
+                        <Select style={{width: "100%"}} placeholder={"Operator"}>
+                            <Option key={1}>==</Option>
+                            <Option key={2}>!=</Option>
+                            <Option key={3}>{">"}</Option>
+                            <Option key={4}>{"<"}</Option>
+                            <Option key={5}>DIFF</Option>
+                        </Select>
+                    </Col>
+                    <Col span={4}>
+                        <Input />
+                    </Col>
+                    <Col span={2}>
+                        <Button.Group>
+                            <Button icon={"delete"} type={"link"} onClick={(e) => this.deleteCondition(path)}/>
+                        </Button.Group>
+                    </Col>
+                </Row>
+            }/>
+        );
+    }
+
+    addRule(nodePath) {
+        nodePath = nodePath.replace(/^\./, '');
+        let data = this.state.data;
+        let children = get(data,nodePath.concat("children"));
+        children = children.push({type:"rule",id: uuidv1()});
+        data = update(data,nodePath.concat(".children"),children);
+        this.setState({data: data});
+    }
+
+    addCondition(nodePath, condition="or") {
+        nodePath = nodePath.replace(/^\./, '');
+        let data = this.state.data;
+        let children = get(data,nodePath.concat("children"));
+        children = children.push(
+            {
+                type:"condition",
+                id: uuidv1(),
+                name: condition,
+                children:[{type: "rule", id: uuidv1()}]}
+        );
+        data = update(data,nodePath.concat(".children"),children);
+        this.setState({data: data});
+    }
+
+    deleteCondition(nodePath) {
+        nodePath = nodePath.replace(/^\./, '');
+        let data = this.state.data;
+        unset(data, nodePath);
+        console.log(data);
+        this.setState({data:data});
+    }
+
+    buildConditions(node, canDelete=true, parent="") {
+        if(node===null) {
+            return("");
+        }
+        return(
+            <TreeNode title={
+                <Row type={"flex"} justify={"start"} align={"middle"}>
+                    <Col>
+                    <Tag color={"red"}>{node.name.toUpperCase()}</Tag>
+                    </Col>
+                    <Col>
+                        <Button.Group>
+                            <Button type={"link"}
+                                    onClick={(e) => this.addRule(parent)}
+                            >Add Rule</Button>
+                            {
+                                parent === "" &&
+                                <Button.Group>
+                                    <Button
+                                        type={"link"}
+                                        onClick={(e) => this.addCondition(parent,"and")}
+                                    >Add And</Button>
+                                    <Button
+                                    type={"link"}
+                                    onClick={(e) => this.addCondition(parent, "or")}
+                                    >Add Or</Button>
+                                </Button.Group>
+                            }
+
+                            { canDelete &&
+                            <Button icon={"delete"} type={"link"}
+                                    onClick={(e) => this.deleteCondition(parent)}
+                            />}
+                        </Button.Group>
+                    </Col>
+                </Row>
+            } key={parent.concat(`.id=${node.id}`)}>
+                {
+                    node.children.map(
+                        (child,index) => {
+                            if(child===null) {
+                                return("");
+                            }
+                            if(child.type === "rule") {
+                                return(this.buildRule(child, parent.concat(`.children[${index}]`)))
+                            } else {
+                                return(this.buildConditions(child,true, parent.concat(`.children[${index}]`)))
+                            }
+                        }
+                    )
+                }
+            </TreeNode>
+        );
+    }
+
+    render() {
+        let data = JSON.parse(JSON.stringify(this.state.data));
+        return(
+            <Tree
+                showLine={true}
+                showIcon={true}
+                defaultExpandAll={true}
+                switcherIcon={<Icon type="down" width={"2rem"} height={"2rem"}/>}
+                onFocus={()=>{}}
+                filterTreeNode={()=>{return false;}}
+            >
+                {this.buildConditions(data, false, "")}
+            </Tree>
+        );
+    }
+}
+
+ConditionsComponent.propTypes = {
+    className: PropTypes.string,
+    conditions: PropTypes.object.isRequired,
+    onAdd: PropTypes.func.isRequired
+};
+
+ConditionsComponent.defaultProps = {
+    className: "conditions-component",
+    conditions: DEFAULT_DATA
+};
