+@import 'shared-resources/style/mixin.scss';
+
+body {
+  //font-family: sans-serif, "Helvetica Neue For Number", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei", "Helvetica Neue", Helvetica, Arial, sans-serif;
+  font-size: small;
+  line-height: normal;
+  //color: var(--white)
+}
+
+.query-builder {
+  overflow: hidden;
+  *, *::before, *::after {
+    box-sizing: border-box;
+  }
+}
+
+
+/******************************************************************************/
+/** COMMON ********************************************************************/
+/******************************************************************************/
+
+
+body.qb-dragging {
+  .ant-tooltip {
+    display: none;
+  }
+}
+
+.query-builder {
+  //font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
+  //font-size: 14px;
+  line-height: 1.25;
+  margin: 1rem;
+}
+
+.query-builder.qb-dragging {
+  cursor: -webkit-grabbing !important;
+  cursor: grabbing !important;
+  button {
+    pointer-events: none;
+  }
+}
+
+.group {
+  background: var(--white);
+  border: 1px solid var(--grey3);
+}
+
+.rule {
+  background-color: var(--white);
+  border: 1px solid transparent;
+  padding: 10px;
+}
+
+.group-or-rule {
+  border-radius: 5px;
+  position: relative;
+}
+
+
+.qb-placeholder {
+  border: 1px dashed var(--grey3);
+}
+
+.group-or-rule-container {
+  margin-bottom: 10px;
+  padding-right: 10px;
+}
+
+/* slider */
+.ant-tooltip-inner {
+  min-height: 18px;
+}
+.ant-slider {
+  margin-bottom: 4px;
+  margin-top: 4px;
+}
+.ant-slider-with-marks {
+  margin-bottom: 10px;
+  margin-top: 4px;
+}
+.ant-slider-track {
+  visibility: visible !important;
+}
+
+/* tree */
+.ant-select-tree-dropdown > div[role="listbox"] {
+  outline: none;
+}
+ul.ant-select-selection__rendered {
+  margin-right: 11px;
+}
+
+/******************************************************************************/
+/** GROUP *********************************************************************/
+/******************************************************************************/
+
+
+.group--children {
+  padding-left: 24px;
+
+  & > .group-or-rule-container > .group-or-rule {
+    &:before {
+      top: -4px;
+      border-width: 0 0 2px 2px;
+    }
+
+    &::after {
+      top: 50%;
+      border-width: 0 0 0 2px;
+    }
+
+    &::before, &::after {
+      content: '';
+      position: absolute;
+      left: -14px;
+      width: 14px;
+      height: calc(50% + 8px);
+      border-color: var(--white);
+      border-style: solid;
+      -webkit-box-sizing: border-box;
+      -moz-box-sizing: border-box;
+      box-sizing: border-box;
+    }
+  }
+
+
+  & > .group-or-rule-container:first-child > .group-or-rule {
+    &::before {
+      top: -12px;
+      height: calc(50% + 14px);
+    }
+  }
+
+  & > .group-or-rule-container:last-child > .group-or-rule {
+    &::before {
+      border-radius: 0 0 0 4px;
+    }
+    &::after {
+      display: none;
+    }
+  }
+
+}
+
+.group--children.hide--line {
+  & > .group-or-rule-container > .group-or-rule {
+    &::before, &::after {
+      border-color: var(--grey3);
+    }
+  }
+}
+
+
+.group--header,
+.group--footer {
+  padding: {
+    top: 10px;
+    right: 5px;
+    bottom: 10px;
+    left: 10px;
+  }
+}
+
+.group--conjunctions.hide--conj {
+  opacity: 0.3;
+}
+
+
