+import * as PropTypes from 'prop-types';
+import { connect } from 'react-redux';
+import debounce from 'lodash/debounce';
+import {Select} from 'antd';
+import React from "react";
+import {getError, getLoading} from "utils/loadingUtils";
+
+const {Option} = Select;
+
+export class SelectRestapiHelper extends React.PureComponent {
+
+    constructor(props) {
+        super(props);
+        this.state = {
+            search_value: ""
+        };
+
+        this.getLoading = this.getLoading.bind(this);
+        this.fetchData = this.fetchData.bind(this);
+        this.handleSearch = this.handleSearch.bind(this);
+        this.mapOptions = this.mapOptions.bind(this);
+        this.onChange = this.onChange.bind(this);
+    }
+
+    getLoading() {
+        if(!getLoading(this.props.rest_api, this.props.uri, this.props.method, "0") &&
+            !getError(this.props.rest_api, this.props.uri, this.props.method, "0")) {
+            return false;
+        }
+        return true;
+    }
+
+    componentDidMount() {
+        // get the data pre loaded
+        this.fetchData(this.state.search_value);
+    }
+
+    fetchData(inputValue, pageSize=undefined) {
+        // this is going to be the rest api dispatch call that happens
+        this.props.fetchData(
+            {
+                filter:{
+                    ...this.state.more_filters,
+                    partial: {
+                        [this.props.searchField]: inputValue
+                    }
+                },
+                page_size: pageSize
+            }
+        )
+    }
+
+    handleSearch(value) {
+        this.setState({search_value: value}, () => this.fetchData(value));
+    }
+
+    mapOptions() {
+        const {rest_api, uri, method, searchField, createOption, additionalOptions} = this.props;
+        if(this.getLoading()) {
+            return("");
+        } else {
+            let options = rest_api[uri][method]["0"].data.records.map(
+                option => (
+                    <Option value={option.id} key={option.id} label={option[searchField]}>
+                        {option[searchField]}
+                    </Option>
+                )
+            );
+            if(createOption && this.state.search_value !== "") {
+                options.unshift(
+                    <Option
+                        value={`create:${this.state.search_value}`}
+                        key={this.state.search_value}
+                        label={this.state.search_value}>
+                        Create {this.state.search_value}
+                    </Option>
+                );
+            }
+            options.push(
+                ...additionalOptions.map(
+                    add => (
+                        <Option
+                            value={add.id}
+                            key={add.id}
+                            label={add[searchField]}
+                        >
+                            {add[searchField]}
+                        </Option>
+                    )
+                )
+            );
+            return options;
+        }
+    }
+
+    onChange(value) {
+        const {rest_api, uri, method, searchField, mode, createOption} = this.props;
+        if(mode==="default") {
+            const entry = rest_api[uri][method]["0"].data.records.filter(
+                option => option.id === value
+            );
+            if(entry.length > 0) {
+                return this.props.onChange({label: entry[0][searchField], value: entry[0].id})
+            } else {
+                return({label: value, value: value});
+            }
+
+        } else {
+            let entry = rest_api[uri][method]["0"].data.records.filter(
+                option => value.includes(option.id)
+            );
+            // handle options created on the fly
+            let createdValues = [];
+            if(createOption) {
+                createdValues = value.filter(val => val.toString().includes("create:")).map(
+                    val => ({label: val.replace("create:",""), value: val.replace("create:","")})
+                );
+            }
+            return this.props.onChange(
+                entry.map(
+                    ent =>  ({label: ent[searchField], value: ent.id})
+                ).unshift(...createdValues)
+            )
+        }
+
+    }
+
+    render() {
+
+        return(
+            <Select
+                {...this.props}
+                labelInValue={true}
+                showArrow={true}
+                showSearch={true}
+                filterOption={false}
+                loading={this.getLoading()}
+                onSearch={debounce(this.handleSearch, 300)}
+                onChange={this.props.onChange}
+                onSelect={(value,obj)=>{console.log(obj)}}
+            >
+                {this.mapOptions()}
+            </Select>
+        );
+    }
+}
+
+SelectRestapiHelper.propTypes = {
+    className: PropTypes.string.isRequired,
+    rest_api: PropTypes.object.isRequired,
+    uri: PropTypes.string.isRequired,
+    fetchData: PropTypes.func.isRequired,
+    onChange: PropTypes.func.isRequired,
+    searchField: PropTypes.string.isRequired,
+    moreFilters: PropTypes.object.isRequired,
+    method: PropTypes.oneOf(["list","bulk"]),
+    mode: PropTypes.oneOf(["default","multiple","tags"]),
+    createOption: PropTypes.bool.isRequired,
+    additionalOptions: PropTypes.array
+};
+
+SelectRestapiHelper.defaultProps = {
+    className: "select-restapi-helper",
+    searchField: "name",
+    moreFilters: {},
+    method: "list",
+    onChange: (option) => {},
+    mode: "default",
+    createOption: false,
+    additionalOptions: []
+};
