+                                            />
+                                        </Col>
+                                        <Col span={2}>
+                                            <Button type={"icon"} icon={"delete"}/>
+                                        </Col>
+                                    </Row>
+                                </Form.Item>
+                            )
+                        )}
+                </Form>
+                <Button type={"link"} onClick={(e) => {
+                    let assignees = this.state.assignees;
+                    assignees.push("New User");
+                    this.setState({assignees: assignees});
+                }}> + Add</Button>
+                <Divider/>
+                <Text strong>
+                    Notification Settings
+                </Text><br/><br/>
+                <Checkbox><Text type={"secondary"}>Notify these people immediately</Text></Checkbox>
+                <Form type={"vertical"}>
+                    <Form.Item label={<Text>Medium</Text>} colon={false}>
+                        <Select
+                            showSearch={true}
+                            filterOption={true}
+                            //mode={"multiple"}
+                            allowClear={true}
+                            showArrow={true}
+                            placeholder={"Select Communication Medium"}
