+import React from 'react';
+import * as PropTypes from 'prop-types';
+import { connect } from 'react-redux';
+import {Row, Col, Drawer, Button, Form, Select, Input, Divider, Typography, Icon, Radio, TimePicker} from 'antd';
+import {SelectRestapi} from "shared-resources/helpers";
+import {mapRestapiStatetoProps,mapRestapiDispatchtoProps} from "redux/maps/restapiMap";
+
+const { Option } = Select;
+const { Text } = Typography;
+
+const CONDITION_STATUS = [
+    "CONDITION_PASS",
+    "CONDITION_FAIL"
+];
+
+const QUESTIONNAIRE_STATUS = [
+    "QUESTIONNAIRE_ANSWERED",
+    "QUESTIONNAIRE_PENDING",
+    "QUESTIONNAIRE_RISK_HIGH",
+    "QUESTIONNAIRE_RISK_LOW",
+    "QUESTIONNAIRE_RISK_MED"
+];
+
+const WORKITEM_STATUS = [
+    "WORKITEM_OPEN",
+    "WORKITEM_CLOSED",
+    "WORKITEM_IN_PROGRESS"
+];
+
+export class WaitNodeComponent extends React.Component {
+
+    constructor(props) {
+        super(props);
+        this.state={
+            selected_dow: [],
+            selected_moy: [],
+            selected_node: undefined,
+            status_options: {
+                condition: CONDITION_STATUS,
+                action_workitem: WORKITEM_STATUS,
+                action_questionnaire: QUESTIONNAIRE_STATUS
+            },
+            check_frequency: undefined
+        };
+        this.getPredecessorNodes = this.getPredecessorNodes.bind(this);
+        this.selectDayOfTheWeek = this.selectDayOfTheWeek.bind(this);
+        this.selectMonthsOfTheYear = this.selectMonthsOfTheYear.bind(this);
+        this.setNodeType = this.setNodeType.bind(this);
+        this.getStatusOptions = this.getStatusOptions.bind(this);
+    }
+
+    getPredecessorNodes() {
+        // the logic here will give all the predecessor nodes. Wait can only happen on a node
+        // that has been executed before it
+        let predecessors = [];
+        let currentNode = this.props.node;
+        const chart = this.props.chart;
+        while(currentNode.type !== "start") {
+            let linkFound = false;
+            Object.keys(chart.links).every(
+                link => {
+                    if(chart.links[link].to.nodeId === currentNode.id) {
+                        predecessors.push(chart.nodes[chart.links[link].from.nodeId]);
+                        currentNode = chart.nodes[chart.links[link].from.nodeId];
+                        linkFound = true;
+                        return false;
+                    }
+                    return true;
+                }
+            );
+            if(!linkFound) {
+                break;
+            }
+
+        }
+        return predecessors;
+    }
+
+    selectDayOfTheWeek(day) {
+        let days = this.state.selected_dow;
+        if(days.includes(day)) {
+            // remove the day
+            days = days.filter(d => d!==day);
+            this.setState({selected_dow: days});
+        } else {
+            days.push(day);
+            this.setState({selected_dow: days});
+        }
+    }
+
+    selectMonthsOfTheYear(month) {
+        let months = this.state.selected_moy;
+        if(months.includes(month)) {
+            // remove the day
+            months = months.filter(m => m!==month);
+            this.setState({selected_moy: months});
+        } else {
+            months.push(month);
+            this.setState({selected_moy: months});
+        }
+    }
+
+    setNodeType(value) {
+        this.setState({selected_node: value.key})
+    }
+
+    getStatusOptions() {
+        const {chart} = this.props;
+        if(!this.state.selected_node) {
+            return [];
+        }
+        switch(chart.nodes[this.state.selected_node].type) {
+            case "action_questionnaire": return QUESTIONNAIRE_STATUS;
+            case "condition": return CONDITION_STATUS;
+            case "action_workitem": return WORKITEM_STATUS;
+            default: return [];
+        }
+    }
+
+    render() {
+        const {visible,onClose, node, chart} = this.props;
+
+        const radioStyle = {
+            display: 'block',
+            height: '30px',
+            lineHeight: '30px',
+        };
+
+        const daysOfTheWeek = ["Mon","Tue","Wed","Thu","Fri","Sat","Sun"];
+        const monthsOfTheYear = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
+
+        const options = this.getStatusOptions();
+
+        return(
+            <Drawer
+                bodyStyle={{overflow: "wrap", minHeight: "1000px", paddingBottom: "30px"}}
+                width={"25%"}
+                title={
+                    <Row gutter={[10,10]}>
+                        <Col span={2}>
+                            <Icon type={node.properties.icon}/>&nbsp;
+                        </Col>
+                        <Col span={18}>
+                            <Text editable={true}>{node.properties.name}</Text>
+                        </Col>
+                    </Row>
+                }
+                placement={"right"}
+                closable={true}
+                onClose={onClose}
+                visible={visible}
+                getContainer={false}
+                style={{ position: 'absolute'}}
+            >
+                <Text type={"secondary"}>
+                    Wait on a state change from a previous action node
+                </Text>
+                <Divider/>
+                <Form type={"vertical"}>
+                    <Form.Item label={"Which action/condition do you want to check status on?"} colon={false}>
+                        <Select
+                            showArrow={true}
+                            showSearch={true}
+                            labelInValue={true}
+                            filterOption={false}
+                            onChange={(value) => this.setNodeType(value)}
+                        >
+                            {
+                                this.getPredecessorNodes().map(
+                                    node => (
+                                        <Option key={node.id} type={node.type}>
+                                            {node.properties.name}({node.type})
+                                        </Option>
+                                    )
+                                )
+                            }
+                        </Select>
+                    </Form.Item>
+                    <Divider/>
+                    <Form.Item label={"What should be the status?"}>
+                        <Select
+                            showArrow={true}
+                            showSearch={true}
+                            labelInValue={true}
+                            filterOption={false}
+                            mode={"multiple"}
+                            //onChange={(value) => this.setNodeType(value)}
+                        >
+                            {
+                                options.map(
+                                    option => (
+                                        <Option key={option}>
+                                            {option.replace("_"," ")}
+                                        </Option>
+                                    )
+                                )
+                            }
+                        </Select>
+                    </Form.Item>
+                    <Divider/>
+                    <Form.Item label={"Check Frequency"} colon={false}>
+                        <Select
+                            showArrow={true}
+                            showSearch={true}
+                            labelInValue={true}
+                            filterOption={false}
+                            onChange={(value) => this.setState({check_frequency: value.key})}
+                        >
+                            <Option key={"monthly"}>Monthly</Option>
+                            <Option key={"weekly"}>Weekly</Option>
+                            <Option key={"daily"}>Daily</Option>
+                        </Select>
+                    </Form.Item>
+                    {
+                        this.state.check_frequency === "weekly" &&
+                        <Form.Item label={"Day(s) of the Week"} colon={false}>
+                            <Button.Group>
+                                {
+                                    daysOfTheWeek.map(
+                                        day => (
+                                            <Button
+                                                type={this.state.selected_dow.includes(day)?"primary":"default"}
+                                                onClick={(e) => this.selectDayOfTheWeek(day)}
+                                            >
+                                                {day}
+                                            </Button>
+                                        )
+                                    )
+                                }
+                            </Button.Group>
+                        </Form.Item>
+                    }
+                    {
+                        this.state.check_frequency === "monthly" &&
+                        <Form.Item label={"Month(s) of the Year"} colon={false}>
+                            <Button.Group>
+                                {
+                                    monthsOfTheYear.map(
+                                        month => (
+                                            <Button
+                                                type={this.state.selected_moy.includes(month)?"primary":"default"}
+                                                onClick={(e) => this.selectMonthsOfTheYear(month)}
+                                            >
+                                                {month}
+                                            </Button>
+                                        )
