+import React from 'react';
+import PropTypes from 'prop-types';
+import { Avatar, Skeleton} from 'antd';
+import { red, gold, green} from '@ant-design/colors'
+
+import './violation-cell.style.scss'
+
+const ViolationCell = (props) => {
+    let {count, description, icons} = props;
+    const low = 30;
+    const high = 70;
+
+    const getBadgeColors = (count) => {
+        if(count < low) {
+            return {color: green[7], backgroundColor: green[1]}
+        } else if(count > high) {
+            return {color: red.primary, backgroundColor: red[1]}
+        } else {
+            return {color: gold[8], backgroundColor: gold[1]}
+        }
+    }
+    
+    return (
+        <div className="cell">
+            <div className="avatar">
+                <Avatar shape="square" style={getBadgeColors(count)}>{count}</Avatar>
+            </div>
+            
+            <div className="percent">
+                <p>% of Total:</p>
+                <p>100.00%(34)</p>
+            </div>
+            <div className="description">
+                {/* <Skeleton active/> */}
+                {description}
+            </div>
+            <div className="icon">
+                {icons.map((icon) => (
+                    <>
+                        {icon}
+                    </>
+                ))}
+            </div>
+        </div>
+    )
+}
+
+ViolationCell.propTypes = {
+    count: PropTypes.number,
+    description: PropTypes.string,
+    icons: PropTypes.array,
+}
+
+export default ViolationCell;
