+import React from 'react';
+import * as PropTypes from 'prop-types';
+import { Icon, Progress } from 'antd';
+import { connect } from 'react-redux';
+
+import ErrorWrapper from 'hoc/errorWrapper';
+import { AntCard, InfiniteScroll } from 'shared-resources/components';
+import { mapPaginationStatetoProps } from 'redux/maps/paginationMap';
+import { getProgressBarColor } from 'utils/progressBarUtils';
+
+export class ProductsInfiniteListCardContainer extends React.PureComponent {
+  componentDidUpdate(prevProps, prevState, snapshot) {
+    if (this.props.pagination_data.length && this.props.pagination_data !== prevProps.pagination_data) {
+      const firstProduct = this.props.pagination_data[0];
+      if (firstProduct.id) {
+        this.props.onSelectProductEvent(firstProduct.id);
+      }
+    }
+  }
+
+  onSelectProductHandler(id) {
+    return () => {
+      this.props.onSelectProductEvent(id);
+    }
+  }
+
+  render() {
+    const mappedProducts = this.props.pagination_data.map((product, index) => ({
+      ...product,
+      score: product.score || 0,
+      progress: product.progress || 100 / index,
+      trend: product.trend || 45,
+      isSelected: product.id === this.props.selected,
+    }));
+
+    return (
+      <div className="products-infinite-list">
+        <InfiniteScroll horizontal restMethod="getProducts" pageSize={10}>
+          {mappedProducts.map(product => (
+            <AntCard
+              style={{
+                width: '23.2rem',
+                height: '13rem',
+                margin: '0 10px',
+                padding: '0 2.4rem',
+                backgroundColor: product.isSelected ? '#fff' : 'transparent'
+              }}
+              key={`product-${product.id}`}
+              bordered={!product.isSelected}
+              onClickEvent={this.onSelectProductHandler(product.id)}
+            >
+              <div className="flex direction-column align-con">
+                <div style={{ padding: '5px 0' }} className="flex direction-row justify-space-between align-center">
+                  <span className="subtitle1">{product.name}</span>
+                  <span className="title1">{product.score}</span>
+                </div>
+                <Progress
+                  percent={product.progress}
+                  showInfo={false}
+                  strokeColor={getProgressBarColor(product.progress, product.isSelected)}
+                  strokeWidth={5}
+                />
+                <div style={{ padding: '5px 0' }} className="flex align-center">
+                  <Icon type={product.trend > 0 ? 'caret-up' : 'caret-down'}
+                        style={{ fontSize: '8px', color: '#b7bcc1' }} />
+                  <span
+                    className="info1">{product.trend > 0 ? `+${product.trend}` : product.trend}% since last month </span>
+                </div>
+              </div>
+            </AntCard>
+          ))}
+        </InfiniteScroll>
+      </div>
+    );
+  }
+}
+
+ProductsInfiniteListCardContainer.propTypes = {
+  onSelectProductEvent: PropTypes.func.isRequired,
+};
+
+export default ErrorWrapper(connect(mapPaginationStatetoProps, null)(ProductsInfiniteListCardContainer))

