+export class AntCardComponent extends React.PureComponent {
+  render() {
+    const defaultStyle = {
+      boxShadow: "0px 2px 3px #1B2C3C1A",
+
+      borderRadius: 5,
+      flexShrink: 0,
+    };
+    const props = this.props;
+    return (
+      <div onClick={this.props.onClickEvent} role="presentation">
+        <Card
+          {...props}
+          style={{ ...defaultStyle, ...props.style }}
+        >
+          {props.children}
+        </Card>
+      </div>
