+            case "output":  className = "workflow-editor__port-inner__io"; break;
+            case "input":   className = "workflow-editor__port-inner__input"; break;
+            case "fail":    className = "workflow-editor__port-inner__left"; break;
+            case "success":   className = "workflow-editor__port-inner__right"; break;
+            default:    className = "workflow-editor__port-inner__io"; break;
+        }
+        return (
+            <div className={`workflow-editor workflow-editor__port`}>
+                <div className={`workflow-editor workflow-editor__port-inner ${className}`}>
+                    {
+                        port.properties.action === "input" &&
+                        <Icon type="caret-right" theme="filled"
+                              style={
+                                  {
+                                      color: "cornflowerblue",
+                                      fontSize: "35px",
+                                  }
+                              }/>
+                    }
+                </div>
+            </div>
+        );
+    }
+}
