+import React from 'react';
+import {
+    PieChart, Pie, Cell, Label, Tooltip
+} from 'recharts';
+
+import { donutColors } from './graph-themes';
+import { mockDonutData } from './graph-mock-data';
+import './donut-chart.style.scss';
+
+export type DonutDataItem = {
+    name: string;
+    value: number;
+    percentage?: number;
+}
+
+type DonutChartProps = {
+    donutChartData: Array<DonutDataItem>
+}
+
+type DonutLabelProps = {
+    bulletColor: string
+    key: string
+}
+
+const DonutChart: React.FC<DonutChartProps> = (props) => {
+
+    const { donutChartData } = props;
+    const itemCount: number = donutChartData.length;
+
+    const colors: Array<string> = itemCount % 5 == 1 ? donutColors.slice(0, - 1) : donutColors;
+    const colorCount: number = colors.length;
+
+    const runningTotal: number = donutChartData.reduce(
+        (acc: number, cur: DonutDataItem): number => (acc + cur.value), 0
+    );
+
+    const chartData: Array<DonutDataItem> = donutChartData.map(item => (
+        {
+            ...item,
+            percentage: Math.round((item.value / runningTotal) * 100),
+        }
+    ));
+
+
+
+    const DonutLabel: React.FC<DonutDataItem & DonutLabelProps> = (props) => {
+        return (
+            <ul>
+                <li>
+                    <span style={{ color: props.bulletColor }}>{` `}</span>
+                    {props.name} | {props.percentage} {`%  `}
+                    {props.value.toLocaleString('en-US')}
+                </li>
+            </ul>
+        )
+    }
+
+    return (
+        <div className="donut-wrapper">
+
+            <h5>Chart Title</h5>
+
+            <PieChart
+                width={800}
+                height={400}
+            >
+                <Pie
+                    data={donutChartData}
+                    cx={120}
+                    cy={200}
+                    innerRadius={76}
+                    outerRadius={96}
+                    startAngle={90}
+                    endAngle={-270}
+                    paddingAngle={5}
+                    dataKey="value"
+                >
+                    <Label
+                        value="Something" position="center" className='label-top' fontSize='27px'
+                    />
+                    <Label
+                        value="10,238" position="center" className='label-bottom'
+                    />
+                    {chartData.map(
+                        (entry: DonutDataItem, index: number) => <Cell
+                            key={`donut-sector-${index}`}
+                            fill={colors[index % colorCount]}
+                        />
+                    )}
+                </Pie>
+                <Tooltip />
+            </PieChart>
+
+            <div className="donutLabels">
+                {chartData.map(
+                    (entry: DonutDataItem, index: number) => <DonutLabel
+                        {...entry}
+                        bulletColor={colors[index % colorCount]}
+                        key={`donut-label-${index}`}
+                    />
+                )}
+            </div>
+
+        </div>
+    )
+}
+
+DonutChart.defaultProps = {
+    donutChartData: mockDonutData
+}
+
+export default DonutChart;
