+.workflow-wrapper {
+  overflow: hidden;
+  .ant-drawer {
+    position: absolute;
+    .ant-input-search {
+      margin-bottom: 8px;
+    }
+    
+  }
+}
+
+.new-node {
+  border: 1px solid var(--border-color);
+  border-radius: 4px;
+}
+.new-node-list + .new-node-list {
+  border-top: 1px solid var(--border-color);
+}
+.new-node-list {
+  padding: 20px;
+  .ant-list-header {
+    padding-top: 0;
+    border-bottom: 0;
+    padding-left: 0;
+    font-size: 12px;
+    font-weight: 600;
+    line-height: 1;
+    letter-spacing: -0.12px;
+    color: var(--btn-color);
+  }
+  .ant-list-item {
+    border: 1px solid var(--border-color);
+    border-radius: 3px;
+    padding: 0;
+    cursor: pointer;
+    &:last-child {
+      border-bottom: 1px solid var(--border-color);
+    }
+    & + .ant-list-item {
+      margin-top: 8px;
+    }
+    .anticon {
+      border-right: 1px solid var(--border-color);
+      padding: 12px;
+      font-size: 16px;
+    }
+    .ant-typography {
+      text-transform: capitalize;
+      padding-left: 12px;
+    }
+  }
+  &__actions {
+    .anticon {
+      color: var(--blue7);
+    }
+  }
+}

